(function(){jQuery.showMessage=function(b,a){if(typeof(b)=='undefined'||b=='')return;settings=jQuery.extend({id:"sliding_message_box",position:"bottom",size:"90",backgroundColor:"rgb(143, 177, 240)",delay:1500,speed:500,fontSize:"30px"},a);a=$("#"+settings.id);if(a.length==0){a=$("<div></div>").attr("id",settings.id);a.css({"z-index":"999","background-color":settings.backgroundColor,"text-align":"center",position:"absolute",position:"fixed",left:"0",width:"100%","line-height":settings.size+"px","height":settings.size+"px","font-size":settings.fontSize});$("body").append(a)}a.html(b);
if(settings.position=="bottom"){a.css("bottom","-"+settings.size+"px");a.animate({bottom:"0"},settings.speed);b='$("#'+settings.id+'").animate({bottom:"-'+settings.size+'px"}, '+settings.speed+");";setTimeout(b,settings.delay)}else if(settings.position=="top"){a.css("top","-"+settings.size+"px");a.animate({top:"0"},settings.speed);b='$("#'+settings.id+'").animate({top:"-'+settings.size+'px"}, '+settings.speed+");";setTimeout(b,settings.delay)}}})(jQuery);